operations.body:
query AllProducts {
  products {
    id
    price {
      cost {
        amount
        currencyCode
        __typename
      }
      __typename
    }
    reviewSummary {
      averageRating
      __typename
    }
    description
    primaryImage
    __typename
  }
}

query ProductById($productId: ID!) {
  product(id: $productId) {
    id
    price {
      cost {
        amount
        currencyCode
        __typename
      }
      __typename
    }
    reviewSummary {
      averageRating
      __typename
    }
    description
    primaryImage
    __typename
  }
}

query BestSellers {
  bestSellers {
    id
    description
    primaryImage
    reviewSummary {
      averageRating
      totalReviews
      __typename
    }
    price {
      cost {
        amount
        currencyCode
        __typename
      }
      __typename
    }
    __typename
  }
}
